mapper.update:
UPDATE BOARD_MASTER_T
SET BM_HIT = BM_HIT + 1
WHERE BM_NO = #{value}

UPDATE BOARD_MASTER_T
SET BM_TITLE = #{bm_title}, BM_CONTENT = #{bm_content}
WHERE BM_NO = #{bm_no} AND
BM_PW = #{bm_pw}

UPDATE BOARD_SUB_T
SET BS_FILE = #{bs_file}
WHERE BM_NO = #{bm_no}

UPDATE BOARD_MASTER_T SET BM_STEP = BM_STEP + 1
WHERE BM_GROUP = #{bm_group}
 AND BM_STEP > #{bm_step}
